import React, { useState } from 'react';
import commentService from '../../services/commentService';
import './CommentForm.css';

const CommentForm = ({ 
  postId, 
  parentId = null, 
  commentId = null,
  initialValue = '', 
  onCommentAdded,
  isReply = false,
  isEdit = false
}) => {
  const [content, setContent] = useState(initialValue);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!content.trim()) return;
    
    setIsSubmitting(true);
    
    try {
      if (isEdit) {
        await commentService.updateComment(commentId, content);
      } else {
        await commentService.addComment(postId, content, parentId);
      }
      
      setContent('');
      if (onCommentAdded) {
        onCommentAdded();
      }
    } catch (error) {
      console.error('Error submitting comment:', error);
      alert('Failed to submit comment. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form className={`comment-form ${isReply ? 'reply-form' : ''}`} onSubmit={handleSubmit}>
      <textarea
        className="comment-input"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder={isReply ? "Write a reply..." : isEdit ? "Edit your comment..." : "Write a comment..."}
        rows={isReply ? 2 : 3}
        required
      />
      <div className="form-actions">
        {!isEdit && (
          <button 
            type="button" 
            className="cancel-button"
            onClick={() => {
              setContent('');
              if (onCommentAdded) onCommentAdded();
            }}
          >
            Cancel
          </button>
        )}
        <button 
          type="submit" 
          className="submit-button"
          disabled={isSubmitting || !content.trim()}
        >
          {isSubmitting ? 'Submitting...' : isEdit ? 'Update' : isReply ? 'Reply' : 'Comment'}
        </button>
      </div>
    </form>
  );
};

export default CommentForm;
