import React from 'react';
import { Link } from 'react-router-dom';
import { FaHeart, FaComment, FaUser } from 'react-icons/fa';
import moment from 'moment';
import './NotificationItem.css';

const NotificationItem = ({ notification, onMarkAsRead }) => {
  const getNotificationIcon = () => {
    switch (notification.type) {
      case 'LIKE':
        return <FaHeart className="notification-icon like" />;
      case 'COMMENT':
        return <FaComment className="notification-icon comment" />;
      case 'FOLLOW':
        return <FaUser className="notification-icon follow" />;
      default:
        return null;
    }
  };
  
  const handleClick = () => {
    if (!notification.read) {
      onMarkAsRead(notification.id);
    }
  };

  return (
    <Link 
      to={notification.link} 
      className={`notification-item ${!notification.read ? 'unread' : ''}`}
      onClick={handleClick}
    >
      <div className="notification-icon-container">
        {getNotificationIcon()}
      </div>
      <div className="notification-content">
        <div className="notification-message">{notification.message}</div>
        <div className="notification-time">{moment(notification.createdAt).fromNow()}</div>
      </div>
      {!notification.read && <div className="unread-indicator"></div>}
    </Link>
  );
};

export default NotificationItem;
